mapper.select:
select username, userid, email, role
from member
where userid = #{userid} and userpwd = #{userpwd}

select username, userid, email, role
from member
where userid = #{userid}

SELECT *
FROM member
WHERE userid = #{userid}
AND userpwd = #{userpwd}

mapper.update:
UPDATE member
SET
userpwd = #{userpwd},
username = #{username},
EMAIL = #{email}
WHERE userid = #{userid}
