mapper.select:
SELECT GROUP_NO
FROM QNA
WHERE QNA_NO=#{qna_no}

SELECT DEPTH
FROM QNA
WHERE QNA_NO=#{qna_no}

SELECT GROUP_ORDER
FROM QNA
WHERE QNA_NO=#{qna_no}

SELECT TITLE
FROM QNA
WHERE QNA_NO=#{qna_no}

mapper.update:
UPDATE QNA
SET TITLE =
#{title},
WRITER = #{writer},
CONTENT = #{content}
WHERE QNA_NO = #{qna_no}

UPDATE QNA
SET GROUP_ORDER = GROUP_ORDER+1
WHERE GROUP_ORDER >= #{group_order}
AND GROUP_NO = #{group_no}
